INSERT INTO "public"."cursos" ("id", "titulo", "descricao", "tema", "data_inicio", "data_fim", "tipo", "estado", "imgcurso", "avaliacao", "dificuldade", "pontos", "requisitos", "publico_alvo", "dados", "informacoes", "video", "alerta_formador", "formador_responsavel", "aprender_no_curso", "idioma", "created_at", "updated_at", "vagas_inscricao") VALUES (48, 'Desenvolvimento Mobile', 'Aprenda a desenvolver aplicações móveis do zero utilizando as principais tecnologias do mercado.', 'Desenvolvimento Mobile', '2025-09-11', '2025-10-11', 'Assíncrono', 'Em breve', 'https://res.cloudinary.com/dogh4530a/image/upload/v1756986095/cursos/lkqafnz8wppqjuwqm7u7.png', NULL, 'Iniciante', 50, '["Conhecimento em programação (qualquer linguagem)","Experiência com desenvolvimento web (recomendado)","Familiaridade com conceitos de UI/UX","Acesso a dispositivo móvel para testes"]', '["Desenvolvedores web que querem expandir para mobile","Programadores com experiência em outras linguagens","Estudantes de informática interessados em mobile","Freelancers que querem oferecer desenvolvimento mobile","Profissionais de tecnologia em transição de carreira"]', '["40+ horas de desenvolvimento prático","Criação de 3 aplicações completas","Deploy nas lojas de aplicações","Integração com APIs reais","Testes em dispositivos reais","Código fonte de todos os projetos","Suporte técnico especializado","Networking com outros desenvolvedores"]', 'Mergulhe no universo do desenvolvimento mobile com este curso completo que aborda as principais tecnologias e frameworks do mercado. Aprenda a criar aplicações nativas e híbridas para Android e iOS com uma abordagem moderna e prática.
